import React, { useForm } from 'react';
import './effects.css';



export const FormWithCustomHook = () => {



    const [ formState, setFormState] = useForm({
        name: '',
        email: '',
        password: ''
    })
    const { name, email, password } = formState;


    const handleInputChange = ({ target }) =>{
        // console.log(e.target.name) para no tener que poner el target name es mejor desestructurarlo del evento
        // console.log(e.target.value)
        setFormState({
            ...formState,
           [ target.name ]: target.value
        })
    }

    return (
        <>
            <h1>FormWithCustomHook</h1>
            <hr />
            <div className="form-group">
                <input
                    type="text"
                    name="name"
                    className="form-control"
                    placeholder="Tu nombre"
                    autoComplete="off"
                    value={ name }
                    onChange={ handleInputChange }
                />


            </div>

            <div className="form-group">
                <input
                    type="text"
                    name="email"
                    className="form-control"
                    placeholder="email"
                    autoComplete="off"
                    value={ email }
                    onChange={ handleInputChange }
                />


            </div>


            <div className="form-group">
                <input
                    type="password"
                    name="password"
                    className="form-control"
                    placeholder="****"
                    autoComplete="off"
                    value={ password }
                    onChange={ handleInputChange }
                />


            </div>


        </>
    )
}
