import React, { useEffect, useState } from 'react';
import './effects.css';
import { Message } from './Message';


export const SimpleForm = () => {



    const [ formState, setFormState] = useState({
        name: '',
        email: ''
    })
    const { name, email} = formState;

    useEffect( () =>{
        console.log('hey');
    }, []);

    useEffect( () =>{
        console.log('formState cambio');
    }, [ formState ]);

    useEffect( () =>{
        console.log('formState cambio email');
    }, [ email ]);

    const handleInputChange = ({ target }) =>{
        // console.log(e.target.name) para no tener que poner el target name es mejor desestructurarlo del evento
        // console.log(e.target.value)
        setFormState({
            ...formState,
           [ target.name ]: target.value
        })
    }

    return (
        <>
            <h1>useEffect</h1>
            <hr />
            <div className="form-group">
                <input
                    type="text"
                    name="name"
                    className="form-control"
                    placeholder="Tu nombre"
                    autoComplete="off"
                    value={ name }
                    onChange={ handleInputChange }
                />


            </div>

            <div className="form-group">
                <input
                    type="text"
                    name="email"
                    className="form-control"
                    placeholder="email"
                    autoComplete="off"
                    value={ email }
                    onChange={ handleInputChange }
                />


            </div>

            { name && Message }
        </>
    )
}
