export const todoReducer = (state = [], action) => {

    switch (action.type) {
        case 'add':

            return [...state, action.payload];

        case 'delete':
            return state.filter(todo => todo.id !== action.payload.id); //payload 123535 action.payload.id regresa un nuevo arreglo

        default:
            return state;
    }

}
