const inicialState = [{
    id: 1,
    todo: 'Comprar leche',
    done: false
}];

const todoReducer = ( state = inicialState , action ) =>{
    return state;
}


let todos = todoReducer();

// no utilizar el push 


const newTodo = {
    id: 2,
    todo: 'Comprar leche',
    done: false
}

let todos2 = todoReducer(newTodo);
