import React from 'react'
import { useFetch } from '../../hooks/useFetch';
import '../02-useEffect/effects.css';

export const MultipleCustomHooks = () => {

    const { loading , data } = useFetch(`https://www.breakingbadapi.com/api/quotes/1`);
    const { autor, quote } = !!data && data[0]; // si viene la data, osea si es true entonces en la posición 0 sino esas dos variables tendran un undefind
    // !null daría true con lo cual no habría data , si pones !!null sería false con lo cual si habría data por eso se pone !!data
    console.log(autor, quote);
    return ( 
    <div>
        <h1> BreakingBad Quotes</h1> 
        <hr />

        {
            loading
            ?
            (
                <div className="alert alert-info text-center">
                    Loading...
                </div>
            )
            :
            (
                <blockquote className="blockquote text-right">
                <p className="mb-0">{ quote }</p>
                <footer className="blockquote-footer">{ autor }</footer>
                </blockquote>
            )

        }


    </div>
    )
}
