import React, { useContext } from 'react';
import { UserContext } from './UserContext';

export const LoginScreen = () => {




    const { setUser }= useContext(UserContext);


    const usuarios = {
        id:123,
        nombre: 'Paquita'
    }

    return (
        <div>
            <h1>Login Screen</h1>
            <hr />
            <button
                className="btn btn-primary"
                onClick={ () => setUser( usuarios )}
            >
                Login
            </button>
        </div>
    )
}
